import React, {useRef, useState} from 'react';
import './AuthPage.css';


const AuthPage = () => {

    const loginRef=useRef<HTMLInputElement>(null);
    const passRef=useRef<HTMLInputElement>(null);

    async function fetchAuth(e:React.MouseEvent) {
        e.preventDefault();
        let login:string=loginRef.current?.value.trim(),
            password:string=passRef.current?.value;

        if(login===''){
           alert('Введите логин');
        }
        else{


            if(password===''){
                alert('Введите пароль');
            }
        }




    }




    return (
        <div>
            <h1>Пожалуйста авторизируйтесь</h1>
            <form action="">

                <input ref={loginRef} placeholder="Логин" type="text"/>
                <input ref={passRef} placeholder="Пароль" type="password"/>
                <button onClick={fetchAuth} type="submit">Войти</button>
            </form>

        </div>
    );
};

export default AuthPage;
