import {SET_USER_DATA} from "./authConstants";

interface AuthState {
    isAuth: boolean;
    login: string;
}


let initialState:AuthState = {
    isAuth: false,
    login:''
}
interface actionType{
    type:string,
    payload:string
}


export const authReducer = (state = initialState, action:actionType) => {
    switch (action.type) {
        case SET_USER_DATA:
            return {
                ...state,
                ...action.payload,
            }
        default:
            return state;
    }
}
