import React, {useRef, useState} from 'react';
import './AuthPage.css';
import axios from "axios";
import {IAuth} from "../../types/types";
import {useDispatch} from "react-redux";


const AuthPage = () => {

    const loginRef=useRef<HTMLInputElement>(null);
    const passRef=useRef<HTMLInputElement>(null);
    const dispatch = useDispatch();

    async function fetchAuth(e:React.MouseEvent) {
        e.preventDefault();
        let login:string|undefined=loginRef.current?.value.trim(),
            password:string|undefined=passRef.current?.value;

        if(login===''){
           alert('Введите логин');
        }
        else{
            if(login!.length>12){
                alert('Длина логина должна быть не более 12 символов!');
            }
            else if(password===''){
                alert('Введите пароль');
            }
            else{
                const response =await axios.get<IAuth>('http://localhost:3000/profile')
                if(response.data.login==login && response.data.password==password){
                   sessionStorage.setItem('isAuth','true');
                   sessionStorage.setItem('userLogin',login);
                }
            else{
                alert('Неверный логин или пароль');
                }

            }


        }


    }


    return (
        <div>
            <h1>Пожалуйста авторизируйтесь</h1>
            <form action="">

                <input ref={loginRef} placeholder="Логин" type="text"/>
                <input ref={passRef} placeholder="Пароль" type="password"/>
                <button onClick={fetchAuth} type="submit">Войти</button>
            </form>

        </div>
    );
};

export default AuthPage;
