import {SET_USER_DATA} from "./authConstants";



interface AuthState {
    isAuth: boolean;
    login: string;
}


let initialState:AuthState = {
    isAuth: false,
    login:''
}




export const authReducer = (state = initialState, action) => {
    console.log(action);
    switch (action.type) {
        case SET_USER_DATA:
            return {
                ...state,
                //...action.payload,
            }
        default:
            return state;
    }
}
