interface AuthState {
    isAuth: boolean;
    login: string;
}


let initialState:AuthState = {
    isAuth: false,
    login:''
}
interface actionProps {
    type:string,
    login:string,
    password:string
}



export const authReducer = (state = initialState, action:actionProps) => {
